import { Component } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
// import { FaTwitterSquare } from '@fortawesome/free-solid-svg-icons'
import { FaTwitterSquare } from "react-icons/fa";
import './App.css';

class App extends Component {
  constructor(){
    super();
    this.state = {
      data: [],
      current: 0

    }
  }

  componentDidMount() {
    fetch("https://type.fit/api/quotes")
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        this.setState({ data: data });
      });
  } 

  changeQuote = () => {
    let current = Math.floor(Math.random() * this.state.data.length)
    this.setState({current})
  }

  render(){
    const {current, data } = this.state;
//     const text = data[current].text;
//     const author = data[current].author;
// console.log(text, author)
    const content = data.filter(el => {
      if(el.author = "Thomas Edison"){
        return (<>
          <h1 id="text">{el.text}</h1>
          <h2 id="by">{el.author}</h2>
          </>)
      }
    })

    return (
      <div className="wrapper">
          <div className="box">
              <div className="inner-box">
                  {/* <i className="fas fa-quote-left"></i> */}
                  <div className="quote">
                    {content}
                    {/* <h1 id="text">{text}</h1>
                    <h2 id="by">{author}</h2> */}
                  </div>
                  <div className="twitter">
                      <button id="next" onClick={this.changeQuote}>Next quote</button>
                      {/* <i className="fab fa-twitter-square"></i> */}
                      <FontAwesomeIcon icon={FaTwitterSquare} />
                  </div>
              </div>
          </div>
      </div>
    );
  }
}

export default App;
